.little {	color: #9B9B9B; font-family: Verdana, Arial, Helvetica;	font-size: 8pt; }
.alert { color: #DD0000; font-family: Verdana, Arial, Helvetica;	font-size: 8pt; }
.alert p {margin:0;}
.littlebutton {	background-color: #BED6E0;	border: 1px solid #92B8CB;	color: #153D51;	font-family: Verdana, Arial, Helvetica;	font-size: 8pt;	text-decoration: none;}
.littletablerow {		color: #153D51;	border-bottom: 1px solid #cccccc;	font-family: Verdana, Arial, Helvetica;	font-size: 8pt;}
.littletableheader { background: #EBEBEB; 	border-bottom: 1px solid #cccccc;	color: #999999;	font-family: Verdana, Geneva, Arial, Helvetica, sans-serif;	font-size: 8pt;	font-weight: bold;}
.mainbackground {	/* background: #F6F9FB; */ }
