.button {	background-color: #E4E4E4; border: 1px solid #CCCCCC;	color: #153D51;	font-family: Verdana, Arial, Helvetica; font-size: 10px; font-weight: normal;	text-decoration: none;}
.button_red {	background-color: #FF3300;	border: 1px solid #CC3300; color: White;	font-family: Verdana, Arial, Helvetica;	font-size: 10px; font-weight: normal;	text-decoration: none;}
.button_red {	background-color: #FF3300;	border: 1px solid #CC3300; color: White;	font-family: Verdana, Arial, Helvetica;	font-size: 10px; font-weight: normal;	text-decoration: none;}
form { margin: 0; padding: 0; } 
input, select, textarea  { FONT-FAMILY: Verdana, Arial, sans-serif; margin: 0; padding: 2px; font-size: 11px;  border: 1px solid #92B8CB; border-left:2px solid #92B8CB;}
.micro, .micro a { font: 9px "Trebuchet ms",Verdana,Tahoma,Arial,sans-serif; color: #bbb; font-weight: normal;}
tr.odd {background-color:inherit;}
